
function scrollbar(outer,inner,sliderwrap){var difference=$(inner).width()-$(outer).width();if(difference>0){$(sliderwrap).show();var handleWidth=50;$(sliderwrap+' .slider-h').slider({orientation:'horizontal',min:0,max:100,value:0,slide:function(event,ui){var leftValue=-(ui.value/100*difference);$(inner).css({left:leftValue});},change:function(event,ui){var leftValue=-(ui.value/100*difference);$(inner).css({left:leftValue});}});$(sliderwrap+' .ui-slider-handle').css({width:handleWidth});var origSliderWidth=$(sliderwrap).attr('data-orig-slider-width');if(!origSliderWidth){origSliderWidth=$(sliderwrap+' .slider-h').width();$(sliderwrap).attr('data-orig-slider-width',origSliderWidth);}
var sliderWidth=origSliderWidth-handleWidth;$(sliderwrap+' .ui-slider').css({width:sliderWidth});}else{$(sliderwrap).hide();}
$(sliderwrap+' .ui-slider').unbind('click');$(sliderwrap+' .ui-slider').click(function(event){event.stopPropagation();});$(outer+', '+sliderwrap+' .slider-wrap').unbind('mousewheel');$(outer+', '+sliderwrap+' .slider-wrap').mousewheel(function(event,delta){var speed=5;var sliderVal=$(sliderwrap+' .slider-h').slider('value');sliderVal+=(delta*speed);$(sliderwrap+' .slider-h').slider('value',sliderVal);event.preventDefault();});}